mapper.select:
SELECT userId, password, email, gender, birthDay, originCountry, livingCountry, livingTown, aboutMe,
       profileImageName, profileImagePath
FROM users WHERE userId = #{userId} AND isDeactivated = 'N';

mapper.update:
UPDATE users SET gender = #{updateRequest.gender}, livingCountry = #{updateRequest.livingCountry},
                 livingTown = #{updateRequest.livingTown}, aboutMe = #{updateRequest.aboutMe}
    WHERE userId = #{userId}
